<?php/** * SocketLabs HTTP injection API sample for merging messages**/header('Content-Type: application/json');define('SOCKETLABS_URL', 'https://inject.socketlabs.com/api/v1/email');define('SOCKETLABS_SERVERID', 'YOUR SERVER ID HERE');define('SOCKETLABS_KEY', 'YOUR API KEY HERE');//build merge data$merge_data = new stdClass();$merge_data->PerMessage=array(  array(array('Field'=>'DeliveryAddress', 'Value'=>'person1@example.com'), array('Field'=>'FavoriteColor', 'Value'=>'blue')),  array(array('Field'=>'DeliveryAddress', 'Value'=>'person2@example.com'), array('Field'=>'FavoriteColor', 'Value'=>'green')));// Create JSON object to POST to SocketLabs$data = new stdClass();$data->ServerId=SOCKETLABS_SERVERID;$data->ApiKey=SOCKETLABS_KEY;$data->Messages= array(array('MergeData'=>$merge_data,                        'Subject'=>'Test',                        'To'=>array(array('EmailAddress'=>'%%DeliveryAddress%%')),                        'From'=>array('EmailAddress'=>'sender@example.com'),                        'HtmlBody'=>'<h1>The HTML portion of the message</h1><br/>Your favorite color is %%FavoriteColor%%.'                      )); $bodyJson = json_encode($data);//echo $bodyJson;//use CURL to POST the JSON to SocketLabs$ch = curl_init();curl_setopt($ch, CURLOPT_URL,SOCKETLABS_URL);curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1 );curl_setopt($ch, CURLOPT_POST,           1 );curl_setopt($ch, CURLOPT_POSTFIELDS,     $bodyJson ); curl_setopt($ch, CURLOPT_HTTPHEADER,     array('Content-Type: application/json')); $result=curl_exec ($ch);curl_close ($ch);//process resultif ($result==false) {  echo 'HTTP Error';} else {    $result_obj = json_decode($result);    if ($result_obj->ErrorCode=='Success') {       echo 'Successfully sent all messages.';    } elseif ($result_obj->ErrorCode=='Warning') {       echo 'At least one message was sent, but there are errors with one or more messages, or their recipients.';        //not shown here, but you can traverse the MessageResults object to get more information on each message that encountered an error or warning    } else {       echo 'Failure Code: ' . $result_obj->ErrorCode;    }}?>